import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useLocation } from 'react-router-dom';
import Homepage from './components/Homepage';
import EducationCareer from './components/EducationCareer';
import Gallery from './components/Gallery';
import IdeasProjects from './components/IdeasProjects';
import Contact from './components/Contact';
import './App.css';

function Navigation() {
  const location = useLocation();
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  
  // Prevent body scrolling when mobile menu is open
  useEffect(() => {
    if (isMobileMenuOpen) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = 'unset';
    }
    
    // Cleanup function to restore scrolling when component unmounts
    return () => {
      document.body.style.overflow = 'unset';
    };
  }, [isMobileMenuOpen]);
  
  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  const closeMobileMenu = () => {
    setIsMobileMenuOpen(false);
  };
  
  return (
    <>
      <nav className="navbar">
        <div className="navbar-container">
          <Link to="/" className="navbar-brand" onClick={closeMobileMenu}>
            My Portfolio
          </Link>
          <ul className="navbar-nav">
            <li>
              <Link 
                to="/" 
                className={`nav-link ${location.pathname === '/' ? 'active' : ''}`}
              >
                Home
              </Link>
            </li>
            <li>
              <Link 
                to="/education-career" 
                className={`nav-link ${location.pathname === '/education-career' ? 'active' : ''}`}
              >
                Education & Career
              </Link>
            </li>
            <li>
              <Link 
                to="/gallery" 
                className={`nav-link ${location.pathname === '/gallery' ? 'active' : ''}`}
              >
                Gallery
              </Link>
            </li>
            <li>
              <Link 
                to="/ideas-projects" 
                className={`nav-link ${location.pathname === '/ideas-projects' ? 'active' : ''}`}
              >
                Ideas & Projects
              </Link>
            </li>
            <li>
              <Link 
                to="/contact" 
                className={`nav-link ${location.pathname === '/contact' ? 'active' : ''}`}
              >
                Contact
              </Link>
            </li>
          </ul>
          <div className={`burger-menu ${isMobileMenuOpen ? 'active' : ''}`} onClick={toggleMobileMenu}>
            <div className="burger-line"></div>
            <div className="burger-line"></div>
            <div className="burger-line"></div>
          </div>
        </div>
      </nav>
      
      {/* Mobile Menu Overlay */}
      <div className={`mobile-menu ${isMobileMenuOpen ? 'active' : ''}`}>
        <button className="mobile-menu-close" onClick={closeMobileMenu}>
          ✕
        </button>
        <ul className="mobile-nav">
          <li>
            <Link 
              to="/" 
              className={`nav-link ${location.pathname === '/' ? 'active' : ''}`}
              onClick={closeMobileMenu}
            >
              Home
            </Link>
          </li>
          <li>
            <Link 
              to="/education-career" 
              className={`nav-link ${location.pathname === '/education-career' ? 'active' : ''}`}
              onClick={closeMobileMenu}
            >
              Education & Career
            </Link>
          </li>
          <li>
            <Link 
              to="/gallery" 
              className={`nav-link ${location.pathname === '/gallery' ? 'active' : ''}`}
              onClick={closeMobileMenu}
            >
              Gallery
            </Link>
          </li>
          <li>
            <Link 
              to="/ideas-projects" 
              className={`nav-link ${location.pathname === '/ideas-projects' ? 'active' : ''}`}
              onClick={closeMobileMenu}
            >
              Ideas & Projects
            </Link>
          </li>
          <li>
            <Link 
              to="/contact" 
              className={`nav-link ${location.pathname === '/contact' ? 'active' : ''}`}
              onClick={closeMobileMenu}
            >
              Contact
            </Link>
          </li>
        </ul>
      </div>
    </>
  );
}

function App() {
  return (
    <Router>
      <div className="App">
        <Navigation />
        <Routes>
          <Route path="/" element={<Homepage />} />
          <Route path="/education-career" element={<EducationCareer />} />
          <Route path="/gallery" element={<Gallery />} />
          <Route path="/ideas-projects" element={<IdeasProjects />} />
          <Route path="/contact" element={<Contact />} />
        </Routes>
        <footer className="footer">
          <div className="container">
            <p>&copy; 2024 My Personal Portfolio. All rights reserved. | <Link to="/contact" className="nav-link" style={{ color: '#ffd700', textDecoration: 'underline' }}>Contact</Link></p>
          </div>
        </footer>
      </div>
    </Router>
  );
}

export default App;
